        return (
            <div className="pion"></div>
        )
    }

    const changeColor = (e) => {
        console.log(e.target)
        /*if (e.target.classList.contains('clicked')) {
            return (
                <div className="pion"></div>
            )
        }*/
        e.target.classList.add('clicked')
    }

    return (
        <div className="rondCases" onClick={changeColor}>
            <Pion />
            <Pion />
            <Pion />
        </div>
    )
}

export default Case;
